mapper.select:
SELECT requestId, fromUserId, toUserId, status, requestedAt, respondedAt
FROM CoupleRequest
WHERE fromUserId = #{userId} OR toUserId = #{userId}

    AND status = #{status}

mapper.update:
UPDATE CoupleRequest
SET status = 'APPROVED', respondedAt = NOW()
WHERE requestId = #{requestId}

UPDATE CoupleRequest
SET status = 'DECLINED', respondedAt = NOW()
WHERE requestId = #{requestId}

UPDATE User
SET coupleId = #{coupleId}
WHERE userId = #{userId}
